&lt;%* let title = tp.file.title;
if (title.startsWith(&ldquo;Untitled&rdquo;)) { title = await tp.system.prompt(&ldquo;제목을 입력해주세요&rdquo;);
if (!title || title.trim() === &quot;&quot;) { const file = tp.file.find_tfile(tp.file.path(true)); await app.vault.trash(file, false); return; } await tp.file.rename(title); }
const slug = title .toLowerCase() .replace(/\s+/g, &lsquo;-&rsquo;) .replace(/[^a-z0-9-]/g, &lsquo;&rsquo;);
const nowISO = tp.date.now(&ldquo;YYYY-MM-DDTHH:mm:ssZ&rdquo;); const created = tp.date.now(&ldquo;YYYY-MM-DD&rdquo;); const updated = tp.file.last_modified_date(&ldquo;YYYY-MM-DD&rdquo;);
tR += `&mdash; title: &ldquo;${title}&rdquo; date: ${nowISO} draft: true slug: &ldquo;${slug}&rdquo; tags: [] categories: [] summary: &quot;&quot; weight: 1 bookHidden: false# ${title}# `; %&gt;
